import React, { Component } from 'react'
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Projects from '../components/Projects';

class SearchBar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            searchInput: '',
            value: 'All'
        }
    }

    handleChange = (e) => {
        this.setState({ searchInput: e.target.value });
    }

    render() {
        const { searchInput } = this.state;
        const filteredProjects = this.props.data.filter(project =>
            (searchInput === '' || project.title.toLowerCase().includes(searchInput.toLowerCase()))
        );

        return (
            <div>
                <InputGroup>
                    <Form.Control as="textarea" aria-label="With textarea" />
                    <InputGroup.Text id="basic-addon1">Filter by:</InputGroup.Text>
                    <Form.Control as="select" onChange={this.handleSelectChange}>
                        <option value="All">All</option>
                        <option value="1">Name</option>
                        <option value="2">P</option>
                        <option value="3">3</option>
                        <option value="100">100</option>
                    </Form.Control>
                </InputGroup>
            </div>
        )
    }
}

export default SearchBar;
