import React, { Component } from "react";
import axios from "axios";
import "./reset.css";
import "./styles/App.scss";
import Header from "./components/Structural/Header";
import Footer from "./components/Structural/Footer";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Profile from "./components/Profile/Profile";
import MyStuff from "./components/MyStuff/MyStuff";
import Projects from "./components/Projects/Projects";
import Landingpage from "./components/LandingPage/LandingPage";
import { ChakraProvider } from "@chakra-ui/react";
import { AnimatePresence } from "framer-motion";
import { useLocation } from 'react-router-dom';

// import { Nav, Navbar } from "react-bootstrap";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      profileData: {},
      currentPath: "/",
    };
  }

  componentDidMount = () => {
    this.loadProfileData();
  };

  loadProfileData = async () => {
    try {
      let profile = `${process.env.REACT_APP_SERVER}/profile`;

      let response = await axios.get(profile);

      this.setState(
        {
          profileData: response.data,
        },
        () => {
          document.title = `${this.state.profileData.basic_info.name}`;
        }
      );
    } catch (error) {
      console.log("Error loading profile data: ", error);
      alert(error.message);
      return;
    }
  };

  render() {
    console.log(this.state);
    // this.titles = [];
    // if (this.state.profileData) {
    //   var name = this.state.profileData.projects.name;
    //   this.titles = this.state.profileData.basic_info.titles
    //     .map((x) => [x.toUpperCase(), 1500])
    //     .flat();
    // }
    // console.log(this.titles);

const location = useLocation();

// if (!element) return null;

    return (
      <ChakraProvider>
        <Router>
          <Header profileData={this.state.profileData.basic_info} />
          <main>
            <Landingpage profileData={this.state.profileData} />
            <main>
              {React.cloneElement(element, { key: location.pathname })}
            </main>
          </main>
          <AnimatePresence initial={false}>
            <Routes location={location}>
              <Route
                exact
                path="/profile"
                element={<Profile profileData={this.state.profileData} />}
              />
              <Route
                path="/mystuff"
                element={<MyStuff profileData={this.state.profileData} />}
              />
              <Route
                exact
                path="/projects"
                element={
                  <Projects
                    profileData={this.state.profileData}
                    projectData={this.state.profileData}
                  />
                }
              />
            </Routes>
          </AnimatePresence>

          <Footer />
        </Router>
      </ChakraProvider>
    );
  }
}

export default App;
