import React, { Component } from "react";
import "./reset.css";
import "./styles/App.scss";
import $ from "jquery";
import Header from "./components/Structural/Header";
import Main from "./components/Structural/Main";
import Footer from "./components/Structural/Footer";

class App extends Component {
  // titles = [];
  constructor(props) {
    super(props);
    this.state = {
      profileData: {},
      projectData: [],
    };
  }

  componentDidMount() {
    this.loadResumeFromPath(`profile.json`);
    this.loadProjectFromPath(`projects.json`);
  }

  loadResumeFromPath = (path) => {
    $.ajax({
      url: path,
      dataType: "json",
      cache: false,
      success: (data) => {
        this.setState({ profileData: data });
      },
      error: function (xhr, status, err) {
        alert(err);
      },
    });
  };

  loadProjectFromPath = (path) => {
    $.ajax({
      url: path,
      dataType: "json",
      cache: false,
      success: function (data) {
        this.setState({ projectData: data }, () => document.title = `${this.state.projectData.basic_info.name}`);
      }.bind(this),
      error: function (xhr, status, err) {
        alert(err);
      },
    });
  };

  render() {
    return (
      <div className="MASTER-DIV">
        <Header profileData={this.state.profileData}
              projectData={this.state.projectData}/>
        <Main profileData={this.state.profileData}
              projectData={this.state.projectData}/>
        <Footer />
      </div>
    );
  }
}

export default App;
