import React, { Component } from "react";
import { LinkContainer } from 'react-router-bootstrap'

// import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import $ from "jquery";
import About from "./About";
import Footer from "./Footer";
import Header from "./Header";
import Home from "./Home";
import Practice from "./Practice";
import Projects from "./Projects";
import { Button } from "react-bootstrap";
import Skills from "./Skills";

export default class Main extends Component {
  constructor(props) {
    super(props);
    this.state = {
      resumeData: {},
      sharedData: {},
    };
  }

  componentDidMount() {
    this.loadSharedData();
    this.loadResumeFromPath(`res_primaryLanguage.json`);
  }

  loadResumeFromPath = (path) => {
    $.ajax({
      url: path,
      dataType: "json",
      cache: false,
      success: (data) => {
        this.setState({ resumeData: data });
      },
      error: function (xhr, status, err) {
        alert(err);
      },
    });
  };

  loadSharedData = () => {
    $.ajax({
      url: `portfolio_shared_data.json`,
      dataType: "json",
      cache: false,
      success: (data) => {
        this.setState({ sharedData: data }, () => {
          document.title = `${this.state.sharedData.basic_info.name}`;
        });
      },
      error: function (xhr, status, err) {
        alert(err);
      },
    });
  };

  render() {
    const { resumeData, sharedData } = this.state;

    return (
        <>
<div>

                <Greeting />

</div>
<LinkContainer to="/profile">
            <Profile as={Button}                 resumeBasicInfo={resumeData.basic_info}
/>
</LinkContainer>
<LinkContainer to="/skills">
            <Skills as={Button} />
</LinkContainer>
<LinkContainer to="/stackprogress">
            <StackProgress as={Button} />
</LinkContainer>
<LinkContainer to="/education">
            <Education as={Button} />
</LinkContainer>
<LinkContainer to="/workexperience">
            <WorkExperience as={Button} />
</LinkContainer>
<LinkContainer to="/achievements">
            <Achievements as={Button} />
</LinkContainer>
<LinkContainer to="/projects">
            <Projects as={Button} />
</LinkContainer>
      <Router>
        <Header sharedData={sharedData.basic_info} />
        <Routes>
          <Route
            exact
            path="/"
            element={<Home resumeData={resumeData} sharedData={sharedData} />}
          />
          <Route
            path="/about"
            element={
              <About
                resumeBasicInfo={resumeData.basic_info}
                sharedBasicInfo={sharedData.basic_info}
              />
            }
          />
          <Route
            path="/projects2"
            element={
              <Projects2
                resumeProjects={resumeData.projects2}
                resumeBasicInfo={resumeData.basic_info}
              />
            }
          />
          <Route
            path="/practice"
            element={
              <Practice
                resumeBasicInfo={resumeData.basic_info}
                sharedBasicInfo={sharedData.basic_info}
              />
            }
          />
        </Routes>
        <Footer sharedBasicInfo={sharedData.basic_info} />
      </Router>
              </>

    );
  }
}
