import React, { Component } from 'react'
import $ from "jquery";
import ProjectDetailsModal2 from './ProjectDetailsModal2';

class Projects2 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      deps: {},
      detailsModalShow2: false,
      resumeData: {},
      sharedData: {},
    };
    console.log(this.state.deps);
  }
  componentDidMount = () => {
    this.loadSharedData();
    this.loadResumeFromPath(`res_primaryLanguage.json`);
  }
  loadResumeFromPath = (path) => {
    $.ajax({
      url: path,
      dataType: "json",
      cache: false,
      success: function (data) {
        this.setState({ resumeData: data });
      }.bind(this),
      error: function (xhr, status, err) {
        alert(err);
      },
    });
  }

  loadSharedData = () => {
    $.ajax({
      url: `portfolio_shared_data.json`,
      dataType: "json",
      cache: false,
      success: function (data) {
        this.setState({ sharedData: data }, () => document.title = `${this.state.sharedData.basic_info.name}`);
      }.bind(this),
      error: function (xhr, status, err) {
        alert(err);
      },
    });
  }
  render() {
    let detailsModalShow2 = (data) => {
      this.setState({ detailsModalShow2: true, deps: data });
      console.log(data);
    };

    let detailsModalClose2 = () => this.setState({ detailsModalShow2: false });

    if (this.props.resumeProjects && this.props.resumeBasicInfo) {
      var sectionName = this.props.resumeBasicInfo.section_name.projects2;
      console.log(this.props.resumeBasicInfo.section_name.projects2)
      var projects2 = this.props.resumeProjects2.map(function (projects2) {
        return (
          <div
            className="col-sm-12 col-md-6 col-lg-4"
            key={projects2.title}
            style={{ cursor: "pointer" }}
          >
            <Projects
              resumeProjects={this.props.resumeData.projects}
              resumeBasicInfo={this.props.resumeData.basic_info}
            />
            <span className="portfolio-item d-block">
              <div className="foto" onClick={() => detailsModalShow2(projects2)}>
                <div>

                  <img
                    src={projects2.images[0]}
                    {...console.log(projects2.images[0])}
                    alt="projectImages"
                    height="230"
                    style={{ marginBottom: 0, paddingBottom: 0, position: 'relative' }}
                  />
                  <span className="project-date">{projects2.startDate}</span>
                  <br />
                  <p className="project-title-settings mt-3">
                    {projects2.title}
                  </p>
                </div>
              </div>
            </span>
          </div>
        );
      });
    }

    return (
      <section id="portfolio2">
        <div className="col-md-12">
          <h1 className="section-title" style={{ color: "black" }}>
            <span>{sectionName}</span>
          </h1>
          <div className="col-md-12 mx-auto">
            <div className="row mx-auto">{projects2}</div>
          </div>
          <ProjectDetailsModal2
            show={this.state.detailsModalShow2}
            onHide={detailsModalClose2}
            data={this.state.deps}
          />
        </div>
      </section>
    );
  }
}

export default Projects2;
