import React, { Component } from "react";
import { Icon } from "@iconify/react";
import Row from './'

import bootstrapIcon from "@iconify/icons-logos/bootstrap";
import reactIcon from "@iconify/icons-logos/react";
import cssIcon from "@iconify/icons-logos/css";
import htmlIcon from "@iconify/icons-logos/html";
import nodejsIcon from "@iconify/icons-logos/nodejs";
import vscodeIcon from "@iconify/icons-logos/vscode";
import sqlIcon from "@iconify/icons-logos/sql";
import mongodbIcon from "@iconify/icons-logos/mongodb";
import jqueryIcon from "@iconify/icons-logos/jquery";
import bashIcon from "@iconify/icons-logos/bash";


import { Col } from "react-bootstrap";

class About extends Component {
  render() {
    if (this.props.sharedBasicInfo) {
      var profilepic = "images/" + this.props.sharedBasicInfo.image;
    }
    if (this.props.resumeBasicInfo) {
      var sectionName = this.props.resumeBasicInfo.section_name.about;
      var hello = this.props.resumeBasicInfo.description_header;
      var about = this.props.resumeBasicInfo.description;
    }

    return (
      <section id="about">
        <div className="background">
          <div className="transparentbox">
            <section id="portfolio">
              <Row>
                <Col
                  xs={4}
                  className="aboutC"
                  style={{ cursor: 'pointer' }}
                >

                  <h1 style={{ color: "black" }}>
                    <span>{sectionName}</span>
                  </h1>

                  <div className="col-md-6 mb-5 center">
                    <div className="polaroid">
                      <div class="slide-right">
                        <div class="inner">
                          <img
                            height="250px"
                            src={profilepic}
                            alt="Avatar placeholder"
                          />
                        </div>
                      </div>
                      <Icon
                        icon={bootstrapIcon}
                        style={{ fontSize: "400%", margin: "9% 5% 0 5%" }}
                      />
                      <Icon
                        icon={reactIcon}
                        style={{ fontSize: "400%", margin: "9% 5% 0 5%" }}
                      />
                      <Icon
                        icon={cssIcon}
                        style={{ fontSize: "400%", margin: "9% 5% 0 5%" }}
                      />
                      <Icon
                        icon={htmlIcon}
                        style={{ fontSize: "400%", margin: "9% 5% 0 5%" }}
                      />
                      <Icon
                        icon={nodejsIcon}
                        style={{ fontSize: "400%", margin: "9% 5% 0 5%" }}
                      />
                      <Icon
                        icon={vscodeIcon}
                        style={{ fontSize: "400%", margin: "9% 5% 0 5%" }}
                      />
                      <Icon
                        icon={bashIcon}
                        style={{ fontSize: "400%", margin: "9% 5% 0 5%" }}
                      />
                    </div>
                  </div>
                </Col>
              </Row>

              <Row>
                <Col
                  xs={4}
                  className="aboutC"
                  style={{ cursor: 'pointer' }}
                >
                  <h1 style={{ color: "black" }}>
                    <span>{sectionName}</span>
                  </h1>
                  <div class="slide-left">
                    <div class="inner">
                      <img
                        height="250px"
                        src={profilepic}
                        alt="Avatar placeholder"
                      />
                    </div>
                  </div>
                  <Icon
                    icon={sqlIcon}
                    style={{ fontSize: "400%", margin: "9% 5% 0 5%" }}
                  />
                  <Icon
                    icon={mongodbIcon}
                    style={{ fontSize: "400%", margin: "9% 5% 0 5%" }}
                  />
                  <Icon
                    icon={jqueryIcon}
                    style={{ fontSize: "400%", margin: "9% 5% 0 5%" }}
                  />
                </Col>
              </Row>
            </section>
          </div>
        </div>
      </section>
    );
  }
}

export default About;
