import React, { Component } from 'react'
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Projects from '../components/Projects';

class SearchBar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            searchInput: '',
            value: 'All'
        }
    }

    handleChange = (e) => {
        this.setState({ searchInput: e.target.value });
    }

    render() {
        const { searchInput } = this.state;
        const filteredProjects = this.props.data.filter(project =>
            (searchInput === '' || project.title.toLowerCase().includes(searchInput.toLowerCase()))
        );

        return (
            <div>
            <Stack>
                    <div className="Beast-choices">
                        {filteredBeasts.map((beast, i) => (
                            <Col key={i}>
                                <HornedBeast beastInfo={beast} />
                            </Col>
                        ))}
                    </div>
            </Stack>
                <InputGroup>
                    <Form.Control as="textarea" aria-label="With textarea" />
                    <InputGroup.Text id="basic-addon1">Filter by:</InputGroup.Text>
                    <Form.Control as="select" onChange={this.handleSelectChange}>
                        <option value="All">All</option>
                        <option value="name">Name</option>
                        <option value="poplarity">Popularity</option>
                        <option value="recent">Recent</option>
                        <option value="size">Size</option>
                    </Form.Control>
                    <Form.Control
                        type="search"
                        onChange={this.handleChange}
                        placeholder="Search"
                        aria-label="Search"
                        aria-describedby="basic-addon1"
                        value={searchInput}
                    />
                </InputGroup>
            </div>
        )
    }
}

export default SearchBar;
