import React, { Component } from "react";
import { Navbar, NavItem } from "react-bootstrap";
import Nav from "react-bootstrap/Nav";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Landingpage from "./LandingPage";
import MyStuff from "./MyStuff";
import Projects2 from "./Projects2";

class Header extends Component {
  titles = [];

  // constructor() {
  //   super();
  //   this.state = { checked: false };
  //   this.onThemeSwitchChange = this.onThemeSwitchChange.bind(this);
  // }

  // onThemeSwitchChange(checked) {
  //   this.setState({ checked });
  //   this.setTheme(checked);
  // }

  // setTheme(checked) {
  //   var dataThemeAttribute = "data-theme";
  //   var body = document.body;
  //   var newTheme = checked ? "dark" : "light";
  //   body.setAttribute(dataThemeAttribute, newTheme);
  // }

  render() {
    if (this.props.sharedData) {
      var name = this.props.sharedData.name;
      this.titles = this.props.sharedData.titles; //...map(x => [ x.toUpperCase(), 1500 ] ).flat();
    }

    const HeaderTitleTypeAnimation = React.memo(() => {
      return this.titles;
    });

    return (
      <header
        id="landingpage"
        style={{ height: window.innerHeight - 100, display: "block" }}
      >
        <Navbar
          activeKey="/landingpage"
          fill
          style={{ position: "absolute", top: 10, right: 10 }}
        >
          <Router>
            <Nav>
              <Navbar />
              <Routes>
                <NavItem>
                  <Route path="/projects" element={<Projects2 resumeProjects={PropTypes.array.isRequired}
    resumeBasicInfo={PropTypes.object.isRequired}/>} />
                </NavItem>
                <NavItem>
                  <Route path="/mystuff" element={<MyStuff />} />
                </NavItem>
                <NavItem>
                  <Route path="/landingpage" element={<Landingpage />} />
                </NavItem>
              </Routes>
            </Nav>
          </Router>
        </Navbar>
        <div className="row aligner" style={{ height: "100%" }}>
          <div className="col-md-12">
            <div>
              <span
                className="iconify header-icon"
                data-icon="la:laptop-code"
                data-inline="false"
              ></span>
              <br />
              <h1 className="mb-0">{name}</h1>
              <div className="title-container">
                <HeaderTitleTypeAnimation />
              </div>
            </div>
          </div>
        </div>
      </header>
    );
  }
}

export default Header;
