import React, { Component } from "react";
// import { NavLink } from 'react-router-dom';
import { LinkContainer } from 'react-router-bootstrap';
// import { BrowserRouter as Routes, Route } from "react-router-dom";
import $ from "jquery";
import Profile from "./Profile";
// import Projects from "./Projects";
import { Button } from "react-bootstrap";
import Skills from "./Skills";
// import Greeting from "./Greeting";
import StackProgress from "./StackProgress";
import Education from "./Education";
import WorkExperience from "./WorkExperience";
import Achievements from "./Achievements";
import MyStuff from "./MyStuff";
import Splash from "./Splash";
import ProjectsDynamicSection from "./ProjectsDynamicSection";

export default class Main extends Component {
  constructor(props) {
    super(props);
    this.state = {
      resumeData: {},
      projectData: {}, // added declaration of projectData
    };
  }

  componentDidMount() {
    this.loadResumeFromPath(`res_primaryLanguage.json`);
    this.loadProjectFromPath(`project_primaryLanguage.json`); // added function call to load project data
  }

  loadResumeFromPath = (path) => {
    $.ajax({
      url: path,
      dataType: "json",
      cache: false,
      success: (data) => {
        this.setState({ resumeData: data });
      },
      error: function (xhr, status, err) {
        alert(err);
      },
    });
  };

  loadProjectFromPath = (path) => { // added function to load project data
    $.ajax({
      url: path,
      dataType: "json",
      cache: false,
      success: (data) => {
        this.setState({ projectData: data });
      },
      error: function (xhr, status, err) {
        alert(err);
      },
    });
  };

  render() {
    const { resumeData, projectData } = this.state; // added projectData to destructuring
    return (
      <>
      <Router>
        <Navbar />
        <main>
          <Routes>
            <Route path="/bookshelf" element={<Bookshelf />}></Route>

            <Route path="/login" element={<Login />}></Route>

            <Route path="/logout" element={<Logout />}></Route>

            <Route path="/" element={<Landingpage />}></Route>

            <Route path='/about' element={<About />}></Route>
          </Routes>
        </main>
        <Footer />
      </Router>
        {/* <div>
          <Greeting />
        </div> */}
        <div>
          <Splash />
        </div>
        <LinkContainer to="/profile">
          <Profile as={Button} resumeBasicInfo={resumeData.basic_info} />
        </LinkContainer>
        <div>
          {" "}
          {/* This will be a dynamic section which moves through each of the sections below, but stays on the same page*/}
          <LinkContainer to="/skills">
            <Skills as={Button} />
          </LinkContainer>
          <LinkContainer to="/stackprogress">
            <StackProgress as={Button} />
          </LinkContainer>
          <LinkContainer to="/education">
            <Education as={Button} />
          </LinkContainer>
          <LinkContainer to="/workexperience">
            <WorkExperience as={Button} />
          </LinkContainer>
          <LinkContainer to="/achievements">
            <Achievements as={Button} />
          </LinkContainer>
          <LinkContainer to="/projects">
            <ProjectsDynamicSection
              as={Button}
              resumeBasicInfo={resumeData.basic_info}
              projectBasicInfo={projectData.basic_info}
            />
          </LinkContainer>
        </div>
        {/* <LinkContainer to="/projects">
          <Projects
            as={Button}
            resumeBasicInfo={resumeData.basic_info}
            projectBasicInfo={projectData.basic_info}
          />
        </LinkContainer> */}

        <LinkContainer to="/mystuff">
          <MyStuff as={Button} />
        </LinkContainer>
      </>
    );
  }
}
