import React, { Component } from "react";
import { LinkContainer } from "react-router-bootstrap";
import { BrowserRouter as router, rout } from "react-router-dom";
import $ from "jquery";
import Profile from "./Profile";
// import Projects from "./Projects";
import { Button } from "react-bootstrap";
import Skills from "./Skills";
// import Greeting from "./Greeting";
import StackProgress from "./StackProgress";
import Education from "./Education";
import WorkExperience from "./WorkExperience";
import Achievements from "./Achievements";
import MyStuff from "./MyStuff";
import Splash from "./Splash";
import ProjectsDynamicSection from "./ProjectsDynamicSection";

export default class Main extends Component {
  constructor(props) {
    super(props);
    this.state = {
      resumeData: {},
    };
  }

  componentDidMount() {
    this.loadResumeFromPath(`res_primaryLanguage.json`);
  }

  loadResumeFromPath = (path) => {
    $.ajax({
      url: path,
      dataType: "json",
      cache: false,
      success: (data) => {
        this.setState({ resumeData: data });
      },
      error: function (xhr, status, err) {
        alert(err);
      },
    });
  };

  render() {
    const { resumeData } = this.state;
    const { projectData } = this.state;
    return (
      <>
        {/* <div>
          <Greeting />
        </div> */}
        <div>
          <Splash />
        </div>
        <LinkContainer to="/profile">
          <Profile as={Button} resumeBasicInfo={resumeData.basic_info} />
        </LinkContainer>
        <div>
          {" "}
          {/* This will be a dynamic section which moves through each of the sections below, but stays on the same page*/}
          <LinkContainer to="/skills">
            <Skills as={Button} />
          </LinkContainer>
          <LinkContainer to="/stackprogress">
            <StackProgress as={Button} />
          </LinkContainer>
          <LinkContainer to="/education">
            <Education as={Button} />
          </LinkContainer>
          <LinkContainer to="/workexperience">
            <WorkExperience as={Button} />
          </LinkContainer>
          <LinkContainer to="/achievements">
            <Achievements as={Button} />
          </LinkContainer>
          <LinkContainer to="/projects">
            <ProjectsDynamicSection
              as={Button}
              resumeBasicInfo={resumeData.basic_info}
              projectBasicInfo={projectData.basic_info}
            />
          </LinkContainer>
        </div>
        {/* <LinkContainer to="/projects">
          <Projects
            as={Button}
            resumeBasicInfo={resumeData.basic_info}
            projectBasicInfo={projectData.basic_info}
          />
        </LinkContainer> */}

        <LinkContainer to="/mystuff">
          <MyStuff as={Button} />
        </LinkContainer>
      </>
    );
  }
}
