import React, { Component } from 'react';
import ProjectDetailsModal from './ProjectDetailsModal';

class Projects2 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      deps: {},
      detailsModalShow: false
    };
  }

  detailsModalShow = (data) => {
    this.setState({ detailsModalShow: true, deps: data });
  };

  detailsModalClose = () => this.setState({ detailsModalShow: false });

  render() {
    let projects = [];

    if (this.props.resumeProjects && this.props.resumeBasicInfo) {
      var sectionName = this.props.resumeBasicInfo.section_name.projects2;
      projects = this.props.filteredProjects.length
        ? this.props.filteredProjects
        : this.props.resumeProjects;

      projects = projects.map(function (project) {
        return (
          <div
            className="col-sm-12 col-md-6 col-lg-4"
            key={project.title}
            style={{ cursor: "pointer" }}
          >
            <span className="portfolio-item d-block">
              <div className="foto" onClick={() => this.detailsModalShow(project)}>
                <div>
                  <img
                    src={project.images[0]}
                    alt="projectImages"
                    height="230"
                    width="368"
                    style={{ marginBottom: 0, paddingBottom: 0, position: 'relative' }}
                  />
                  <span className="project-date">{project.startDate}</span>
                  <br />
                  <p className="project-title-settings mt-3">
                    {project.title}
                  </p>
                </div>
              </div>
            </span>
          </div>
        );
      }, this);
    }
    return (
      <div>
        {/* Render the SearchBar component here */}
        <div className="background">
          <div className="transparentbox">
            <section id="portfolio">
              <div className="col-md-12">
                <h1 className="section-title" style={{ color: "black" }}>
                  <span>{sectionName}</span>
                </h1>
                <div className="col-md-12 mx-auto">
                  <div className="row mx-auto">
                    {/* Render the projects here */}
                  </div>
                </div>
                <ProjectDetailsModal
                  show={this.state.detailsModalShow}
                  onHide={this.detailsModalClose}
                  data={this.state.deps}
                />
              </div>
            </section>
          </div>
        </div>
      </div>
    );
  }
}

export default Projects2;
